<?php/*	This code will delete the record from the look_product table on deletion	of the corresponding product in that record*/class Sigma_Look_Model_Observer extends Mage_Core_Model_Abstract {    public function deleteRecord($observer)    {			        $productCollection = $observer->getEvent()->getProduct();					$sku = $productCollection->getSku();				try {			$lookProducts = Mage::getModel('look/product')->getCollection()->addFieldToFilter('sku',$sku); 			foreach($lookProducts as $lookprod)					$lookprod->delete();		} catch(Exception $e) {			Mage::getSingleton('core/session')->addError('Something went wrong...');		}	}}	?>